using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;namespace High_Low_Game{	class Program	{		static void Main(string[] args)		{			//Welcome message			Console.WriteLine("Welcome to the High Low Game");						//Choose a random number			Random rng = new Random();			int number = rng.Next(0, 11);			int attempts = 0;			while (true)			{				//Enter Guess				int guess = Convert.ToInt32(Console.ReadLine());				attempts = attempts + 1;				//Compare guess to number				if (guess < number)				{					Console.WriteLine("Higher.");				   				}				else if (guess > number)				{					Console.WriteLine("Lower.");									}				else				{					Console.WriteLine("Correct.");					Console.WriteLine(attempts.ToString());					break;				}			}		  			//Wait for a key press			Console.ReadKey();		}	}}